<template>    <div class="shop-cart-list">        <transition name="fade">            <cube-popup                    :maskClosable=true                    v-show="visible"                    @mask-click="maskClick"                    position="bottom"                    type="shopCartPop"                    :z-index=90            >                <transition name="move"                >                    <div v-show="visible"                    >                        <div class="list-header">                            <div class="shopListHeader">                                <span class="title">已选商品</span>                                <span class="clear" @click="empty">清空</span>                            </div>                            <cube-scroll class="listScroll" ref="listContent">                            <ul class="allList">                                <li class="list" v-for="(food,index) in selectFoods" :key="index">                                    <span class="food">{{food.name}}</span>                                    <span class="price">${{food.price}}</span>                                    <cartControlWapper :food="food">                                    </cartControlWapper>                                </li>                            </ul>                            </cube-scroll>                            <div class="cart-icon" :class='{highlight:totalCount >0}'>                                <svg class="icon" aria-hidden="true">                                    <use xlink:href="#icon-htmal5icon29"></use>                                </svg>                            </div>                        </div>                    </div>                </transition>            </cube-popup>        </transition>    </div></template><script>    import cartControlWapper from 'components/cart-control-wapper/cart-control-wapper.vue'    export default {        components: {            cartControlWapper        },        name: 'shopCartList',        data() {            return {                visible: false            }        },        props: {            selectFoods: {                type: Array,                default() {                    return []                }            },            totalCount:{                type:Number,                default(){                    return 0                }            }        },        methods: {            maskClick() {               this.hide()            },            hide(){                this.visible = false                this.$emit('hide')            },            show() {                this.visible = true                this.$nextTick(()=>{                    this.$refs.listContent.refresh()                })            },            empty(){                this.selectFoods.forEach((food)=>{                    food.count= 0                })            }        }    };</script><style lang="stylus" scoped>    .shop-cart-list {        >>>.cube-popup.cube-popup_mask.cube-shopCartPop{            bottom  47px        }        .fade-enter,fade-leave{            opacity 0        }        .fade-enter-active,fade-leave-active{            transition all 1s        }        .list-header {            position relative            .listScroll{                max-height 200px;                overflow hidden            }            .shopListHeader {                border-bottom: .133333vw solid #ddd;                background-color: #eceff1;                color: #666;                align-items: center;                display: flex;                padding 5px                .title {                    flex 8                    font-size 16px                }                .clear {                    flex 1                    font-size 13px                }            }            background-color: #eceff1;            color: #666;            .cart-icon {                bottom 0                &.highlight {                    background-color: #2a85e4                    .icon {                        fill white                    }                }                position: absolute                flex 1                left 20px                bottom -40px                background-color: #444                width 50px;                height 50px;                display flex;                justify-content center;                vertical-align center                border-radius 50%                border: 5px solid #3c3c3c;                .icon {                    fill: #737373;                    margin auto                    width 25px;                    height 25px                }            }    }        .allList {            background: #fff            .list {                display: flex;                align-items: center;                padding 3px                border-top: 1px solid #eee;                padding 5px 25px                .food {                    flex: 5.5;                    line-height: normal;                    padding 5px                }                .price {                    text-align: right;                    white-space: nowrap;                    font-weight: 700;                    color red                    padding 5px                    padding 0 15px                }            }        }    }</style>